from flask import Flask, render_template

app = Flask(__name__)

# Criar a primeira pagina do site
#   route  -> link, caminho onde se encontra a pagina em questão
#   função -> O que voce quer exibir naquela página.    
#   Templete
@app.route("/") 
def homepage():
    return render_template("homepage.html")


@app.route("/contatos")
def contatos():
    return render_template("contatos.html")


@app.route("/usuario/<usuario>")
def usuario(usuario):
    return render_template("usuarios.html",nomeUsuario=usuario)


# Executa somente se estiver rodando o site através destes arquivo
if __name__ == "__main__":
    app.run(debug=True)